package Advanced_Pattern;

public class new_Star4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
       
		int i,j;
		int n=5;
		char ch='A';
		for(i=1;i<=n;i++) {
			
			for(j=1;j<=i;j++) {
				System.out.print(ch+" ");
		
			}
			ch++;
			System.out.println();
		}
	}

}
