package Number_Pattern;

import java.util.Scanner;

// Right angle triangle Number

public class Num3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
         Scanner sc= new Scanner(System.in);
         int i,j;
         int n =sc.nextInt();
         for(i=1;i<=n;i++) {
        	 for(j=1;j<=i;j++) {
        	
        	//	 System.out.print(j);
        		 System.out.print(i);
        	 }
        	 System.out.println();
         }
	
	
	}

}
